var ibtnPlay,btnFav,btnNext,styleFav,styleUnfav,stylePlay,stylePause;
function AddJumpList(){window.external.msSiteModeCreateJumplist("相关网站");window.external.msSiteModeAddJumpListItem("落网电台","http://luoo.net","http://luoo.net/favicon.ico");window.external.msSiteModeShowJumplist()}
function addFuncBtn(){document.addEventListener("msthumbnailclick",onButtonClicked,false);btnPlay=window.external.msSiteModeAddThumbBarButton("/image/pin/playback_pause.ico","暂停");btnFav=window.external.msSiteModeAddThumbBarButton("/image/pin/star_fav_empty.ico","收藏");btnNext=window.external.msSiteModeAddThumbBarButton("/image/pin/playback_next.ico","跳过");window.external.msSiteModeShowThumbBar()}
function addButtonStyles(){styleFav=window.external.msSiteModeAddButtonStyle(btnFav,"/image/pin/star_fav.ico","收藏");styleUnfav=window.external.msSiteModeAddButtonStyle(btnFav,"/image/pin/star_fav_empty.ico","取消收藏");stylePlay=window.external.msSiteModeAddButtonStyle(btnPlay,"/image/pin/playback_play.ico","播放");stylePause=window.external.msSiteModeAddButtonStyle(btnPlay,"/image/pin/playback_pause.ico","暂停")}
function setFavBtnState(a){if(isie9&&window.external.msIsSiteMode())a?window.external.msSiteModeShowButtonStyle(btnFav,styleFav):window.external.msSiteModeShowButtonStyle(btnFav,styleUnfav)}function setPlayBtnState(a){if(isie9&&window.external.msIsSiteMode())a?window.external.msSiteModeShowButtonStyle(btnPlay,stylePause):window.external.msSiteModeShowButtonStyle(btnPlay,stylePlay)}
function hideButtons(a){window.external.msSiteModeUpdateThumbBarButton(btnPlay,true,!a);window.external.msSiteModeUpdateThumbBarButton(btnPause,true,a)}function onButtonClicked(a){switch(a.buttonID){case btnPlay:$("#jplayer_pause").is(":hidden")?$("#jplayer_play").trigger("click"):$("#jplayer_pause").trigger("click");break;case btnFav:$("#fav").trigger("click");break;case btnNext:$("#jplayer_next").trigger("click")}}if(isie9&&window.external.msIsSiteMode()){AddJumpList();addFuncBtn();addButtonStyles()};